import { useState } from 'react';
import { Link } from 'react-router-dom';
import ProductSelector from '../components/ProductSelector';
import ConfigurationSummary from '../components/Configuration';

const Configurator = () => {
  const [configuration, setConfiguration] = useState({});

  const handleProductSelect = (product) => {
    setConfiguration(prevConfig => ({
      ...prevConfig,
      [product.category]: product.name
    }));
  };

  return (
    <div className="configurator">
      <h1>Конфигуратор</h1>
      <Link to="/">Вернуться на главную</Link>
      <div className="configurator-content">
        <ProductSelector 
          products={[
            { id: 1, name: 'Intel Core i5', category: 'Процессор', price: 20000 },
            { id: 2, name: 'AMD Ryzen 5', category: 'Процессор', price: 18000 },
          ]} 
          onSelect={handleProductSelect} 
        />
        <ConfigurationSummary configuration={configuration} />
      </div>
    </div>
  );
};

export default Configurator;
