import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import HomePage from './pages/HomePage';
import Configurator from './pages/Configurator';
import Cart from './pages/Cart';
import Support from './pages/Support';
import { lightTheme, darkTheme } from './themes';

const App = () => {
    const [theme, setTheme] = useState(lightTheme);

    const toggleTheme = () => {
        setTheme(theme === lightTheme ? darkTheme : lightTheme);
    };

    return (
        <Router>
            <div style={{ backgroundColor: theme.background, color: theme.color, minHeight: '100vh', transition: 'background-color 0.3s ease, color 0.3s ease' }}>
                <nav style={{ backgroundColor: theme.navBackground }}>
                    <Link to="/" style={{ color: theme.navColor }}>Главная</Link>
                    <Link to="/configurator" style={{ color: theme.navColor }}>Конфигуратор</Link>
                    <Link to="/cart" style={{ color: theme.navColor }}>Корзина</Link>
                    <Link to="/support" style={{ color: theme.navColor }}>Поддержка</Link>
                    <button onClick={toggleTheme} style={{ backgroundColor: theme.buttonBackground, color: theme.navColor }}>Сменить тему</button>
                </nav>
                <Switch>
                    <Route path="/" exact component={HomePage} />
                    <Route path="/configurator" component={Configurator} />
                    <Route path="/cart" component={Cart} />
                    <Route path="/support" component={Support} />
                </Switch>
            </div>
        </Router>
    );
};

export default App;
