import React, { useState } from 'react';
import ProductCard from '../components/ProductCard';

const products = {
    cpu: [
        { id: 1, name: 'AMD Ryzen 5', price: 15000 },
        { id: 2, name: 'Intel i5', price: 17000 }
    ],
    gpu: [
        { id: 3, name: 'NVIDIA RTX 3060', price: 40000 },
        { id: 4, name: 'AMD RX 580', price: 25000 }
    ],
    // Добавьте остальные категории
};

const Configurator = () => {
    const [cart, setCart] = useState([]);

    const handleAddToCart = (product) => {
        setCart([...cart, product]);
    };

    return (
        <div className="configurator">
            <h1>Конфигуратор ПК</h1>
            <div className="products">
                {Object.keys(products).map(category => (
                    <div key={category}>
                        <h2>{category}</h2>
                        <div className="product-list">
                            {products[category].map(product => (
                                <ProductCard key={product.id} product={product} onAddToCart={handleAddToCart} />
                            ))}
                        </div>
                    </div>
                ))}
            </div>
            <div className="cart">
                <h2>Корзина</h2>
                <ul>
                    {cart.map((item, index) => (
                        <li key={index}>{item.name} - {item.price} руб.</li>
                    ))}
                </ul>
                <p>Итого: {cart.reduce((total, item) => total + item.price, 0)} руб.</p>
            </div>
        </div>
    );
};

export default Configurator;
