    },
    { 
      id: 'gpu', 
      name: 'Видеокарты',
      description: 'Выберите графический процессор' 
    },
    { 
      id: 'storage', 
      name: 'Накопители',
      description: 'Выберите жесткий диск или SSD' 
    }
  ];

  export const PRODUCTS = [
    // Процессоры
    {
      id: 'cpu-1',
      category: 'cpu',
      name: 'AMD Ryzen 5 5600X',
      price: 25000,
      description: '6-ядерный процессор для игр и работы',
      specs: {
        cores: 6,
        threads: 12,
        baseFrequency: 3.7,
        maxFrequency: 4.6,
        socket: 'AM4'
