      manufacturer: 'AMD',
      compatibility: ['motherboard-amd-1', 'motherboard-amd-2'],
      image: '/images/ryzen-5600x.jpg'
    },
    {
      id: 'cpu-2',
      category: 'cpu',
      name: 'Intel Core i5-12600K',
      price: 30000,
      description: '10-ядерный процессор для производительных задач',
      specs: {
        cores: 10,
        threads: 16,
        baseFrequency: 3.7,
        maxFrequency: 4.9,
        socket: 'LGA1700'
      },
      manufacturer: 'Intel',
      compatibility: ['motherboard-intel-1', 'motherboard-intel-2'],
      image: '/images/i5-12600k.jpg'
    },

    // Материнские платы
    {
      id: 'motherboard-amd-1',
      category: 'motherboard',
      name: 'ASUS ROG Strix B550-F Gaming',
      price: 15000,
      description: 'Игровая материнская плата для AMD Ryzen',
      specs: {
        socket: 'AM4',
        formFactor: 'ATX',
        chipset: 'B550',
        memorySlots: 4,
        maxMemory: 128
      },
      compatibility: ['cpu-1', 'ram-1', 'ram-2'],
      image: '/images/asus-b550.jpg'
    },
    {
      id: 'motherboard-intel-1',
      category: 'motherboard',
      name: 'MSI MPG Z690 Carbon WiFi',
      price: 25000,
      description: 'Премиальная материнская плата для Intel',
      specs: {
        socket: 'LGA1700',
        formFactor: 'ATX',
        chipset: 'Z690',
        memorySlots: 4,
        maxMemory: 128
      },
      compatibility: ['cpu-2', 'ram-3', 'ram-4'],
      image: '/images/msi-z690.jpg'
    },

    // Оперативная память
    {
      id: 'ram-1',
      category: 'ram',
      name: 'Corsair Vengeance LPX 16GB DDR4',
      price: 6000,
      description: 'Оперативная память для игровых систем',
      specs: {
        type: 'DDR4',
        size: 16,
        frequency: 3200,
        latency: 'CL16'
      },
      compatibility: ['motherboard-amd-1', 'motherboard-intel-1'],
      image: '/images/corsair-ram.jpg'
    },

    // Видеокарты
    {
      id: 'gpu-1',
      category: 'gpu',
      name: 'NVIDIA GeForce RTX 3070',
      price: 50000,
      description: 'Мощная видеокарта для игр и рендеринга',
      specs: {
        memory: 8,
        memoryType: 'GDDR6',
        baseClock: 1500,
        boostClock: 1725
