import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProductCategories from '../components/ProductCategories';
import FeaturedProducts from '../components/FeaturedProducts';
import Configurator from '../components/Configurator';

const HomePage = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);

  useEffect(() => {
    // Загрузка избранных продуктов
    const loadFeaturedProducts = async () => {
      try {
        // Здесь будет вызов API
        // const products = await ApiService.getFeaturedProducts();
        // setFeaturedProducts(products);
      } catch (error) {
        console.error('Ошибка загрузки продуктов', error);
      }
    };

    loadFeaturedProducts();
  }, []);

  return (
    <div className="home-page container">
      <section className="hero">
        <h1>Соберите идеальный компьютер</h1>
        <p>Создайте мощный и надежный ПК под ваши задачи</p>
        <div className="hero-actions">
          <Link to="/configurator" className="btn btn-primary">
            Начать сборку
          </Link>
          <Link to="/support" className="btn btn-secondary">
            Помощь
          </Link>
        </div>
      </section>

      <ProductCategories />

      <section className="featured-products">
        <h2>Популярные комплектующие</h2>
        <FeaturedProducts products={featuredProducts} />
      </section>

      <section className="quick-configurator">
        <h2>Быстрый конфигуратор</h2>
        <Configurator compact={true} />
      </section>

      <section className="advantages">
        <h2>Почему выбирают нас</h2>
        <div className="advantages-grid">
          <div className="advantage">
            <i className="icon-quality"></i>
            <h3>Качество</h3>
            <p>Только проверенные компоненты</p>
          </div>
          <div className="advantage">
            <i className="icon-support"></i>
            <h3>Поддержка</h3>
            <p>Круглосуточная техническая поддержка</p>
          </div>
          <div className="advantage">
            <i className="icon-price"></i>
            <h3>Цена</h3>
            <p>Лучшее соотношение цена/качество</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
